import React, { useState } from 'react';
import { FiMenu, FiLogOut, FiUser } from 'react-icons/fi';

const Header = ({ onMenuClick, user, onLogout, title = 'لوحة التحكم' }) => {
  const [showUserMenu, setShowUserMenu] = useState(false);

  return (
    <header className="sticky top-0 z-30 bg-white border-b border-gray-200">
      <div className="px-4 py-3 sm:px-6">
        <div className="flex items-center justify-between">

          {/* Left */}
          <div className="flex items-center gap-3">
            <button
              onClick={onMenuClick}
              className="p-2 text-gray-600 hover:text-gray-800 lg:hidden"
            >
              <FiMenu className="w-5 h-5" />
            </button>

            <h1 className="text-lg font-semibold text-gray-800">
              {title}
            </h1>
          </div>

          {/* Right */}
          <div className="relative">
            <button
              onClick={() => setShowUserMenu(!showUserMenu)}
              className="flex items-center gap-2 px-3 py-2 border border-gray-200 rounded-lg hover:bg-gray-50"
            >
              <FiUser className="w-4 h-4 text-gray-600" />
              <span className="text-sm text-gray-700">
                {user?.name || 'المستخدم'}
              </span>
            </button>

            {showUserMenu && (
              <div className="absolute left-0 mt-2 w-40 bg-white border border-gray-200 rounded-lg shadow-lg z-50">
                <button
                  onClick={onLogout}
                  className="flex items-center gap-2 w-full px-4 py-2 text-sm text-red-600 hover:bg-red-50"
                >
                  <FiLogOut />
                  تسجيل الخروج
                </button>
              </div>
            )}
          </div>

        </div>
      </div>
    </header>
  );
};

export default Header;
