import React, { useState, useEffect } from 'react';
import { useTasks } from '../../context/TaskContext';
import TaskItem from './TaskItem';
import TaskFilters from './TaskFilters';
import Button from '../UI/Button';
import Card from '../UI/Card';
import LoadingSpinner from '../UI/LoadingSpinner';
import { FiPlus, FiFilter, FiList, FiGrid, FiRefreshCw } from 'react-icons/fi';

const TaskList = ({ onTaskSelect, onCreateNew }) => {
  const { tasks, loading, pagination, changePage, fetchTasks } = useTasks();
  const [viewMode, setViewMode] = useState('list');

  // إعادة تحميل المهام عند فتح الصفحة
  useEffect(() => {
    if (tasks.length === 0) {
      fetchTasks();
    }
  }, [fetchTasks, tasks.length]);

  if (loading && tasks.length === 0) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (tasks.length === 0) {
    return (
      <Card className="text-center py-12">
        <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
          <FiList className="w-8 h-8 text-gray-400" />
        </div>
        <h3 className="text-lg font-semibold text-gray-900 mb-2">لا توجد مهام</h3>
        <p className="text-gray-600 mb-6">ابدأ بإنشاء أول مهمة لك</p>
        <Button onClick={onCreateNew} startIcon={<FiPlus />}>
          إنشاء مهمة جديدة
        </Button>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div className="flex items-center gap-2">
          <h2 className="text-xl font-bold text-gray-900">المهام</h2>
          <span className="bg-primary-100 text-primary-800 text-sm font-medium px-2.5 py-0.5 rounded-full">
            {pagination.totalItems}
          </span>
        </div>
        
        <div className="flex items-center gap-2">
          <div className="flex bg-gray-100 p-1 rounded-lg">
            <button
              onClick={() => setViewMode('list')}
              className={`p-2 rounded ${viewMode === 'list' ? 'bg-white shadow' : 'text-gray-500'}`}
            >
              <FiList className="w-5 h-5" />
            </button>
            <button
              onClick={() => setViewMode('grid')}
              className={`p-2 rounded ${viewMode === 'grid' ? 'bg-white shadow' : 'text-gray-500'}`}
            >
              <FiGrid className="w-5 h-5" />
            </button>
          </div>

          <Button
            variant="secondary"
            onClick={() => fetchTasks()}
            startIcon={<FiRefreshCw />}
            className="whitespace-nowrap"
          >
            تحديث
          </Button>
          
          <Button
            onClick={onCreateNew}
            startIcon={<FiPlus />}
            className="whitespace-nowrap"
          >
            مهمة جديدة
          </Button>
        </div>
      </div>

      <TaskFilters />

      <div className={viewMode === 'grid' 
        ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4' 
        : 'space-y-4'
      }>
        {/* {tasks.map(task => (
          <TaskItem 
            key={task.id} 
            task={task} 
            viewMode={viewMode}
            onClick={() => onTaskSelect && onTaskSelect(task)}
          />
        ))} */}

{tasks.map(task => (
  <div key={task.id} className="border p-4">
    <h3>{task.title}</h3>
    <p>{task.status}</p>
  </div>
))}
      </div>

      {pagination.totalPages > 1 && (
        <div className="flex justify-center items-center gap-2 pt-4">
          <Button
            variant="secondary"
            size="sm"
            onClick={() => changePage(pagination.currentPage - 1)}
            disabled={pagination.currentPage === 1}
          >
            السابق
          </Button>
          
          <div className="flex items-center gap-1">
            {[...Array(pagination.totalPages)].map((_, idx) => {
              const pageNum = idx + 1;
              const isCurrent = pageNum === pagination.currentPage;
              const showPage = (
                pageNum === 1 ||
                pageNum === pagination.totalPages ||
                (pageNum >= pagination.currentPage - 1 && pageNum <= pagination.currentPage + 1)
              );

              if (!showPage) {
                if (pageNum === pagination.currentPage - 2 || pageNum === pagination.currentPage + 2) {
                  return <span key={pageNum} className="px-2">...</span>;
                }
                return null;
              }

              return (
                <button
                  key={pageNum}
                  onClick={() => changePage(pageNum)}
                  className={`px-3 py-1 rounded-lg text-sm font-medium transition-colors ${
                    isCurrent
                      ? 'bg-primary-600 text-white'
                      : 'text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  {pageNum}
                </button>
              );
            })}
          </div>

          <Button
            variant="secondary"
            size="sm"
            onClick={() => changePage(pagination.currentPage + 1)}
            disabled={pagination.currentPage === pagination.totalPages}
          >
            التالي
          </Button>
        </div>
      )}
    </div>
  );
};

export default TaskList;
